import { HttpClient, HttpParams, HttpResponse } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, lastValueFrom, of } from 'rxjs';
import { map } from 'rxjs/operators';
import { Config } from '../index';
import { ApiService, GlobalMethods, GlobalConstants } from '../../login';
import { DataCacheService } from './data-cache.service';
import { FixedIDs } from 'src/app/shared';
import { ImageFile } from '../models/common.model';

@Injectable({
  providedIn: 'root',
})
export class FileUploadService {
  private baseUrl = Config.url.adminLocalUrl;

  constructor(private http: HttpClient, private apiSvc: ApiService, private dataCacheService: DataCacheService) { }

  // multiple file upload
  uploadFiles(files): any {
    let formData: FormData;
    formData = new FormData();
    for (let i = 0; i < files.length; i++) {
      formData.append('file', files[i], files[i].name);
    }

    return this.http.post(`${this.baseUrl}File/Upload`, formData);
  }

  // single file upload
  uploadFile(url, folderName, fileInfo, file): Observable<any> {
    const formData: FormData = new FormData();
    formData.append('folderName', folderName);
    formData.append('fileInfo', fileInfo);
    formData.append('file', file, file.name);

    const params = {
      folderName: folderName,
      fileInfo: JSON.stringify(fileInfo),
    };

    return this.http.post(`${this.baseUrl}File/UploadFiles`, formData, {
      params: params,
      reportProgress: true,
      observe: 'events',
    });
  }

  // used to check is user choose valid file
  isSelectedFileValid(fileName: any, fileExtensions: any) {
    try {
      let isValid = true;
      if (!fileExtensions) {
        isValid = true;
      } else {
        const sFileExtension = fileName
          .split('.')
        [fileName.split('.').length - 1].toLowerCase();
        const _validFileExtensions = fileExtensions.split(',');
        let blnValid = false;
        for (let j = 0; j < _validFileExtensions.length; j++) {
          const sCurExtension = _validFileExtensions[j];
          if (sCurExtension.indexOf(sFileExtension) > -1) {
            blnValid = true;
            break;
          }
        }
        if (!blnValid) {
          isValid = false;
        }
      }
      return isValid;
    } catch (e) { }
  }

  // used to get current uploaded file virtual path
  getCurrentUploadedFileVirtualPath(requestConfig) {
    try {
      const downloadServiceUrl = `${this.baseUrl}Admin/GetCurrentUploadedFileVirtualPath`;
      const jsonData = JSON.stringify(requestConfig);
      const params = new HttpParams().set('param', jsonData);

      return this.apiSvc.executeQuery(downloadServiceUrl, params).pipe(
        map((response: any) => {
          return response.body;
        })
      );
    } catch (e) { }
  }

  // used to set file thumbnail if file is not image
  setThubnail(fielObject: any) {
    try {
      const imageType = 'image';
      const fileType = 'text';
      const audioType = 'audio';
      const videoType = 'video';
      const applicationType = 'application';

      const textFileStrem = `data:image/png;base64,`;
      /*../Content/images/fileicon/blank.png*/
      const audioFileStrem = `data:image/jpg;base64,`;
      /*../Content/images/fileicon/audioFile.jpg*/
      const videoFileStream = `data:image/png;base64,`;
      /*../Content/images/fileicon/video.png*/
      const pdfFileStrem = `data:image/jpg;base64,`;
      /*../Content/images/fileicon/pdf.jpg*/ const excelFileStrem = `data:image/jpg;base64,`;
      const docFileStrem = `data:image/jpg;base64,`;
      /*../Content/images/fileicon/word.jpg*/

      if (!fielObject.fileSrc) {
      } else if (fielObject.fileSrc.indexOf(imageType) > -1) {
      } else if (fielObject.fileSrc.indexOf(fileType) > -1) {
        fielObject.thubnailSrc = textFileStrem;
      } else if (fielObject.fileSrc.indexOf(audioType) > -1) {
        fielObject.thubnailSrc = audioFileStrem;
      } else if (fielObject.fileSrc.indexOf(videoType) > -1) {
        fielObject.thubnailSrc = videoFileStream;
      } else if (fielObject.fileSrc.indexOf(applicationType) > -1) {
        if (fielObject.fileSrc.indexOf(applicationType + '/pdf') > -1) {
          fielObject.thubnailSrc = pdfFileStrem;
        } else if (fielObject.fileSrc.indexOf('sheet') > -1) {
          fielObject.thubnailSrc = excelFileStrem;
        } else if (fielObject.fileSrc.indexOf('document') > -1) {
          fielObject.thubnailSrc = docFileStrem;
        } else {
          fielObject.thubnailSrc = textFileStrem;
        }
      } else {
        fielObject.thubnailSrc = textFileStrem;
      }
    } catch (e) { }
  }

  // create not found image object
  setNotFoundImage() {
    /* const noImageSrc =
       'data:image/png;base64,';
     */
    const noImageSrc =
      '../../../../assets/images/no-image.png';

    const data = {
      filePath: null,
      fileStream: noImageSrc,
    };
    return data;
  }

  // Used to get image from  application server
  getImageFileRequest(requestConfig) {
    try {
      const jsonData = JSON.stringify(requestConfig);
      const downloadServiceUrl = `${this.baseUrl}Admin/GetImages`;
      const params = new HttpParams().set('param', jsonData);

      return this.apiSvc.executeQuery(downloadServiceUrl, params).pipe(
        map((response: any) => {
          return response.body;
        })
      );
    } catch (e) { }
  }

  //Used to move all tick folder files from temporary folder location to draft folder

  shiftPermanentToTemporaryFiles(tick, sourceFolder, destinationFolder, ids) {
    try {
      if (!ids) {
        ids = '-1';
    }
    if (!sourceFolder || !destinationFolder) {
      return;
    }


      const downloadServiceUrl = `${this.baseUrl}File/ShiftPermanentFilesToTemp`;
      const params = {
        tick: tick,
        userPKID: GlobalConstants.userInfo.userPKID,
        permanentFolder: sourceFolder,
        tempFolder: destinationFolder,
        ids: ids
    }
      return this.apiSvc.executeQuery(downloadServiceUrl, params).pipe(
        map((response: any) => {
          return response.body;
        })
      );
    } catch (e) { }
  }
  
  // Used to get image/file path from  application server
  getPhysicalFilesPath(ids: any, folderName: any) {
    try {
      const downloadServiceUrl = `${this.baseUrl}Admin/GetPhysicalFilesPath`;
      return this.apiSvc
        .executeQuery(downloadServiceUrl, { ids: ids.toString(), folderName: folderName.toString() })
        .pipe(
          map((response: any) => {
            return response.body;
          })
        );
    } catch (e) { }
  }

  // convert base64 image to blob object
  b64toBlob(base64Content, sliceSize) {
    const contentType = 'image/png';
    const b64Data = base64Content.split(',')[1];
    sliceSize = sliceSize || 512;

    const byteCharacters = atob(b64Data);
    const byteArrays = [];

    for (let offset = 0; offset < byteCharacters.length; offset += sliceSize) {
      const slice = byteCharacters.slice(offset, offset + sliceSize);

      const byteNumbers = new Array(slice.length);
      for (let i = 0; i < slice.length; i++) {
        byteNumbers[i] = slice.charCodeAt(i);
      }

      const byteArray = new Uint8Array(byteNumbers);
      byteArrays.push(byteArray);
    }

    const blob = new Blob(byteArrays, { type: contentType });
    const fileName = new Date().getTime() + '.png';
    // blob.name = fileName;
    // return blob;
    const file = new File([blob], fileName, { type: contentType });
    return file;
  }

  // Used to get image from  application server
  async getImageFile(targetObject) {
    try {
      // get image from cache for checking
      // TODO 
      var imageObject = this.getImageFromCache(targetObject.id, targetObject.folderName, targetObject.fileTick, targetObject.isThumbnailFormat);

      // if image exist no need to fatch image from server
      if (!imageObject) {
        const newFileInfo = GlobalMethods.deepClone<any>(targetObject);
        if (typeof newFileInfo.fileTick === 'undefined') {
          newFileInfo.fileTick = null;
        }
        newFileInfo.userID = GlobalConstants.userInfo.userPKID; //100000307; //TODO nedd check userPKID is null
        newFileInfo.fileSrc = null;
        newFileInfo.thubnailSrc = null;

        // this.getImageFileRequest(newFileInfo).subscribe(data => { });

        let data = await lastValueFrom(this.getImageFileRequest(newFileInfo));
        if (!data) {
          data = this.setNotFoundImage();
        }

        targetObject.isUploaded = true;
        targetObject.isThumbnailFormat = true;
        targetObject.creationDate = data.creationDateTime
          ? new Date(data.creationDateTime).toLocaleDateString()
          : null;
        targetObject.creationTime = data.creationDateTime
          ? new Date(data.creationDateTime).toLocaleTimeString()
          : null;
        targetObject.fileSrc = data.fileStream;
        targetObject.filePath = data.filePath;

        this.setThubnail(targetObject);
        // TODO 
        this.createImageCache(targetObject.id, targetObject.folderName,
          data, targetObject.fileTick, targetObject.isThumbnailFormat);
      } else {
        targetObject.isUploaded = true;
        targetObject.isThumbnailFormat = true;
        targetObject.creationDate = imageObject.data.creationDateTime
          ? new Date(imageObject.data.creationDateTime).toLocaleDateString()
          : null;
        targetObject.creationTime = imageObject.data.creationDateTime
          ? new Date(imageObject.data.creationDateTime).toLocaleTimeString()
          : null;
        targetObject.fileSrc = imageObject.data.fileStream;
        targetObject.filePath = imageObject.data.filePath;

        this.setThubnail(targetObject);
      }

    } catch (e) {
      throw e;
    }
  }

  //used to get an image from cache
  getImageFromCache(id, folderName, tick, isThumbnailFormat) {
    try {
      var listOfImage = [];
      var cacheData = [];
      var imageObject = null;
      folderName = tick ? tick + folderName : folderName;
      this.dataCacheService.get(folderName).subscribe({
        next: (res: any) => {
          cacheData = res;
        }
      });
      listOfImage = cacheData == undefined ? [] : cacheData;

      if (listOfImage.length > 0) {
        imageObject = listOfImage.find(x => x.id === id)
      }
      return imageObject;
    } catch (e) {

    }
  }

  removeImageFromCache(id, folderName, tick, isThumbnailFormat) {
    try {
      var imageObject = this.getImageFromCache(id, folderName, tick, isThumbnailFormat);
      if (imageObject) {
        var listOfImage = [];
        var cacheData = [];
        folderName = tick ? tick + folderName : folderName;
        let res = this.dataCacheService.get(folderName);
        if (res) {
          res.subscribe({
            next: (res: any) => {
              cacheData = res;
            }
          });
        }
        listOfImage = cacheData == undefined ? [] : cacheData;
        for (var i = 0; i < listOfImage.length; i++) {
          if (listOfImage[i].id == imageObject.id) {
            listOfImage.splice(i, 1);
            break;
          }
        }
      }
    } catch (e) {

    }
  }

  createImageCache(id, folderName, data, tick, isThumbnailFormat) {
    try {
      var imageObject = this.getImageFromCache(id, folderName, tick, isThumbnailFormat);
      if (!imageObject) {
        var listOfImage = [];
        var cacheData = [];
        //fetch cache
        folderName = tick ? tick + folderName : folderName;
        let res = this.dataCacheService.get(folderName);
        if (res) {
          res.subscribe({
            next: (res: any) => {
              cacheData = res;
            }
          });
        }
        listOfImage = cacheData == undefined ? [] : cacheData;
        imageObject = { id: id, data: data, isThumbnailFormat: isThumbnailFormat ? isThumbnailFormat : false };
        //push image in list
        listOfImage.push(imageObject);
        //push list in cache
        this.dataCacheService.set(folderName, listOfImage);
      }
    } catch (e) {
      throw e;
    }
  }

  // for webcam and single file upload
  singleUploadFile(uploadOption, selectedFile): Observable<any> {
    const userID = GlobalConstants.userInfo.userPKID; //100000307; //TODO nedd check userPKID is null
    const uploadServiceUrl = uploadOption.uploadServiceUrl;
    // those file which are not uploaded and whose tag is 4 (insert mode)
    this.removeImageFromCache(selectedFile.id, selectedFile.folderName, selectedFile.fileTick, true);
    // userInfo.userPKID
    const flObject = {
      userID: userID,
      id: selectedFile.id,
      fileName: selectedFile.fileName,
      progressId: selectedFile.progressId,
      fileTick: selectedFile.fileTick,
    };
    // file upload service
    return this.uploadFile(
      uploadServiceUrl,
      uploadOption.folderName,
      flObject,
      selectedFile.fileObject
    ).pipe(
      map(
      (event) => {
        // file is uploaded successfully
        if (event instanceof HttpResponse) {
          selectedFile.isUploaded = true;
          selectedFile.tag = FixedIDs.objectState.added;
          return selectedFile;
          // this.setCurrentUploadedFileVirtualPath(this.selectedFile);
        }
      }
    ));
  }

  streamDataUploadAsFile(streamData, uploadOptions, targetObject): Observable<any> {
    try {
      let fileName = "";
      if(targetObject.fileName)
      {
        fileName = targetObject.fileName;
      }
      else{
        fileName =  new Date().getTime() + ".pdf";
      }
      let byteData = this.base64ToArrayBuffer(streamData);
      let blobFile = this.byteDataToBlobFile(byteData);
      // file Obj
      targetObject.fileObject = blobFile;
      targetObject.fileObject.name = fileName;
      targetObject.fileName = fileName;
      targetObject.fileChecked = true;
      targetObject.isUploaded = false;

      targetObject.fileSrc = streamData;
      targetObject.filePath = null;
      this.setThubnail(targetObject);
      targetObject.progressId = Math.random().toString().replace('.', '');

      return this.singleUploadFile(uploadOptions, targetObject).pipe(
        map((res:any) => {
          return res;
        })
      );
    } catch (e) {
      throw e;
    }
  }


  byteDataToBlobFile(byteData: Uint8Array) {
    try {
      let blobFile = new Blob([byteData], { type: 'application/octet-stream' });
      return blobFile;
    } catch (e) {
      throw e;
    }
  }

  base64ToArrayBuffer(base64:string): Uint8Array  {
      try {
        const binaryString = window.atob(base64);
        const binaryLen = binaryString.length;
        const bytes = new Uint8Array(binaryLen);
  
        for (let i = 0; i < binaryLen; i++) {
          const ascii = binaryString.charCodeAt(i);
          bytes[i] = ascii;
        }
  
        return bytes;
      }
      catch (e) {
      }
  }

}
